const MOCK_MESSAGES = [
  {
    _id: 3,
    text: 'Hello, John!',
    createdAt: new Date(),
    user: {
      _id: 2,
      name: 'Sue',
    },
  },
  {
    _id: 4,
    text: 'It happened this way that ...',
    createdAt: new Date(),
    user: {
      _id: 2,
      name: 'Sue',
    },
  }
];

const sortedMessages = MOCK_MESSAGES.sort((a, b) => b._id - a._id);

const ChatRoom = (props) => {

  const { chatId, userId } = props.route.params;
  const [messages, setMessages] = useState(sortedMessages);
  const [token, setToken] = useState('');
  const [username, setUsername] = useState('');

  useEffect(() => {
    WebSocketClient.onReceiveMessage = (newMessage) => {
      setMessages((prevMessages) =>
        GiftedChat.append(prevMessages, newMessage)
      );
    };
  }, [messages]);

  useEffect(() => {
    const getTokenAsync = async () => {
      let token = await AsyncStorage.getItem('token');
      setToken(token);
      console.log('Alınan token: ' + token);
      const decodedToken = parseJwt(token);
      setUsername(decodedToken.sub);
      console.log(decodedToken);
      WebSocketClient.connect(token);
    };
  
    getTokenAsync();
  }, []);

  const parseJwt = (token) => {
    const parts = token.split('.').map((part) =>
      Buffer.from(part.replace(/-/g, '+').replace(/_/g, '/'), 'base64').toString()
    );
    const payload = JSON.parse(parts[1]);
    return payload;
  };

  const user = {
    _id: userId,
    name: 'John',
    token: token,
  };
  
  const onSend = (newMessages) => {
  
    const formattedMessages = newMessages.map((message) => {
      return {
        ...message,
        _id: uuid.v4(),
        chatId: chatId,
        user: {
          _id: user._id,
          name: user.name,
          token: token,
          receiver :"husniye@gmail.com"
        },
      };
    });
  
    setMessages((prevMessages) =>
      GiftedChat.append(prevMessages, formattedMessages)
    );
  
    WebSocketClient.send(formattedMessages);
  };

  const renderMessageImage = (props) => {
    const { currentMessage } = props;
    if (currentMessage.image) {
      return (
        <View>
          <Image
            style={styles.messageImage}
            source={{ uri: `data:image/png;base64,${currentMessage.image}` }}
          />
        </View>
      );
    } else {
      return null;
    }
  };

  return (
    <View style={{ flex: 1 }}>
      <GiftedChat
        messages={messages}
        onSend={(newMessages) => onSend(newMessages)}
        user={user}
        renderUsernameOnMessage
        renderMessageImage={renderMessageImage}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  messageImage: {
    width: 200,
    height: 200,
    borderRadius: 10,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textInput: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    width: '50%',
  },
});

export default ChatRoom;